        </div>
        <div style={{margin: "10px 0 0 0"}}>
            <div style={{"opacity": 0.9}}>Email del organizador</div>            
            <input placeholder="opcional" type="email"  onChange={e => room["ownerEmail"] = e.target.value}></input>                                 
        </div>
